import '../styles/editor.css';

import { EditorContent, useEditor } from '@tiptap/react';
import { JSX } from 'react';

import { EditorExtensions } from '../extensions/editor';

export interface EditorProps {
	content?: string;
	onChange?: (content: string) => void;
}

export function Editor({ content = '', onChange }: EditorProps): JSX.Element {
	const editor = useEditor({
		extensions: EditorExtensions,
		content,
		editable: true,
		onUpdate: ({ editor }) => {
			onChange?.(editor.getHTML());
		}
	});

	return (
		<div className="notra-editor">
			<EditorContent editor={editor} />
		</div>
	);
}

import '../styles/editor.css';

import { JSONContent } from '@tiptap/core';
import { renderToReactElement } from '@tiptap/static-renderer/pm/react';
import { JSX } from 'react';

import { ViewerExtensions } from '../extensions/viewer';

export interface ViewerProps {
	content: JSONContent;
}

export function Viewer({ content }: ViewerProps): JSX.Element {
	return (
		<div className="notra-editor">
			{renderToReactElement({ extensions: ViewerExtensions, content })}
		</div>
	);
}

import { HorizontalRule } from './horizontal-rule';
import { Starter } from './starter';

export const EditorExtensions = [Starter, HorizontalRule];

import TiptapHorizontalRule from '@tiptap/extension-horizontal-rule';
import { mergeAttributes } from '@tiptap/react';

// Wrapper div enables better cursor positioning around the rule
export const HorizontalRule = TiptapHorizontalRule.extend({
	renderHTML() {
		return [
			'div',
			mergeAttributes(this.options.HTMLAttributes, { 'data-type': this.name }),
			['hr']
		];
	}
});

import { StarterKit } from '@tiptap/starter-kit';

export const Starter = StarterKit.configure({
	horizontalRule: false,
	codeBlock: false
});

import { HorizontalRule } from './horizontal-rule';
import { Starter } from './starter';

export const ViewerExtensions = [Starter, HorizontalRule];

.notra-editor {
	white-space: pre-wrap;
	outline: none;
	color: var(--notra-text);
}

.notra-editor > * {
	position: relative;
}

.notra-editor {
	blockquote {
		position: relative;
		padding-left: 1em;
		padding-top: 0.375em;
		padding-bottom: 0.375em;
		margin: 1.5rem 0;

		p {
			margin-top: 0;
		}

		/* Decorative left bar via pseudo-element */
		&::before {
			position: absolute;
			bottom: 0;
			left: 0;
			top: 0;
			height: 100%;
			width: 0.25em;
			background-color: var(--notra-blockquote-bar);
			content: '';
		}
	}
}

.notra-editor {
	code {
		background-color: var(--notra-code-bg);
		color: var(--notra-code-text);
		border: 1px solid var(--notra-code-border);
		font-family:
			ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, monospace;
		font-size: 0.875em;
		line-height: 1.4;
		border-radius: 0.375rem;
		padding: 0.1em 0.2em;
	}

	pre {
		background-color: var(--notra-codeblock-bg);
		color: var(--notra-codeblock-text);
		border: 1px solid var(--notra-codeblock-border);
		margin-top: 1.5em;
		margin-bottom: 1.5em;
		padding: 1em;
		font-size: 1rem;
		border-radius: 0.375rem;
		overflow-x: auto;

		code {
			background-color: transparent;
			border: none;
			border-radius: 0;
			color: inherit;
			padding: 0;
		}
	}
}

/* Vertical rhythm values from Tailwind Typography */
.notra-editor {
	h1,
	h2,
	h3,
	h4 {
		position: relative;
		color: inherit;
		font-style: inherit;

		&:first-child {
			margin-top: 0;
		}
	}

	h1 {
		margin-top: 0;
		margin-bottom: 0.888889em;
		font-size: 2.25em;
		font-weight: 700;
		line-height: 1.11111;
	}

	h2 {
		margin-top: 2em;
		margin-bottom: 1em;
		font-size: 1.5em;
		font-weight: 700;
		line-height: 1.33333;
	}

	h3 {
		margin-top: 1.6em;
		margin-bottom: 0.6em;
		font-size: 1.25em;
		font-weight: 600;
		line-height: 1.6;
	}

	h4 {
		margin-top: 1.5em;
		margin-bottom: 0.5em;
		font-weight: 600;
		line-height: 1.5;
	}
}

.notra-editor {
	hr {
		border: none;
		height: 1px;
		background-color: var(--notra-hr);
		margin-top: 2.25em;
		margin-bottom: 2.25em;
	}
}

.notra-editor {
	img {
		max-width: 100%;
		height: auto;
		display: block;
		margin: 2rem 0;
		border-radius: 0.25rem;
	}
}

.notra-editor {
	ol,
	ul {
		margin-top: 1.5em;
		margin-bottom: 1.5em;
		padding-left: 1.5em;

		&:first-child {
			margin-top: 0;
		}

		&:last-child {
			margin-bottom: 0;
		}

		ol,
		ul {
			margin-top: 0;
			margin-bottom: 0;
		}
	}

	li {
		p {
			margin-top: 0;
			line-height: 1.6;
		}
	}

	ol {
		list-style: decimal;

		ol {
			list-style: lower-alpha;

			ol {
				list-style: lower-roman;
			}
		}
	}

	ul {
		list-style: disc;

		ul {
			list-style: circle;

			ul {
				list-style: square;
			}
		}
	}
}

.notra-editor {
	p:not(:first-child) {
		font-size: 1rem;
		line-height: 1.6;
		font-weight: normal;
		margin-top: 1.25em;
	}

	strong {
		font-weight: 700;
	}

	em {
		font-style: italic;
	}

	a {
		color: var(--notra-link);
		text-decoration: underline;
	}

	a span {
		text-decoration: underline;
	}

	s span {
		text-decoration: line-through;
	}

	u span {
		text-decoration: underline;
	}
}

/* Palette - override these to change the theme */
.notra-editor {
	/* Gray alpha scale */
	--notra-gray-a-50: rgb(56 56 56 / 4%);
	--notra-gray-a-100: rgb(15 22 36 / 5%);
	--notra-gray-a-200: rgb(37 39 45 / 10%);
	--notra-gray-a-300: rgb(47 50 55 / 20%);
	--notra-gray-a-400: rgb(40 44 51 / 42%);
	--notra-gray-a-500: rgb(52 55 60 / 64%);
	--notra-gray-a-600: rgb(36 39 46 / 78%);
	--notra-gray-a-700: rgb(35 37 42 / 87%);
	--notra-gray-a-800: rgb(30 32 36 / 95%);
	--notra-gray-a-900: rgb(29 30 32 / 98%);

	/* Brand */
	--notra-brand-50: rgb(239 238 255);
	--notra-brand-100: rgb(222 219 255);
	--notra-brand-200: rgb(195 189 255);
	--notra-brand-300: rgb(157 138 255);
	--notra-brand-400: rgb(122 82 255);
	--notra-brand-500: rgb(98 41 255);
	--notra-brand-600: rgb(84 0 229);
	--notra-brand-700: rgb(75 0 204);
	--notra-brand-800: rgb(56 0 153);
	--notra-brand-900: rgb(43 25 102);
	--notra-brand-950: hsl(257deg 100% 9%);

	/* Semantic tokens */
	--notra-text: var(--notra-gray-a-900);
	--notra-link: var(--notra-brand-500);
	--notra-code-bg: var(--notra-gray-a-100);
	--notra-code-text: var(--notra-gray-a-700);
	--notra-code-border: var(--notra-gray-a-200);
	--notra-codeblock-bg: var(--notra-gray-a-50);
	--notra-codeblock-text: var(--notra-gray-a-800);
	--notra-codeblock-border: var(--notra-gray-a-200);
	--notra-blockquote-bar: var(--notra-gray-a-900);
	--notra-hr: var(--notra-gray-a-200);
}

/* Dark mode via .dark ancestor or class on editor itself */
.dark .notra-editor,
.notra-editor.dark {
	--notra-gray-a-50: rgb(232 232 253 / 5%);
	--notra-gray-a-100: rgb(231 231 243 / 7%);
	--notra-gray-a-200: rgb(238 238 246 / 11%);
	--notra-gray-a-300: rgb(239 239 245 / 22%);
	--notra-gray-a-400: rgb(244 244 255 / 37%);
	--notra-gray-a-500: rgb(236 238 253 / 50%);
	--notra-gray-a-600: rgb(247 247 253 / 64%);
	--notra-gray-a-700: rgb(251 251 254 / 75%);
	--notra-gray-a-800: rgb(253 253 253 / 88%);
	--notra-gray-a-900: rgb(255 255 255 / 96%);
	--notra-link: var(--notra-brand-400);
}

/* Dark mode via system preference (opt-out with .light class) */
@media (prefers-color-scheme: dark) {
	.notra-editor:not(.light) {
		--notra-gray-a-50: rgb(232 232 253 / 5%);
		--notra-gray-a-100: rgb(231 231 243 / 7%);
		--notra-gray-a-200: rgb(238 238 246 / 11%);
		--notra-gray-a-300: rgb(239 239 245 / 22%);
		--notra-gray-a-400: rgb(244 244 255 / 37%);
		--notra-gray-a-500: rgb(236 238 253 / 50%);
		--notra-gray-a-600: rgb(247 247 253 / 64%);
		--notra-gray-a-700: rgb(251 251 254 / 75%);
		--notra-gray-a-800: rgb(253 253 253 / 88%);
		--notra-gray-a-900: rgb(255 255 255 / 96%);
		--notra-link: var(--notra-brand-400);
	}
}
