import { EditorContent, useEditor } from '@tiptap/react';
import { JSX } from 'react';

import { EditorExtensions } from '../extensions/editor';

export interface EditorProps {
	content?: string;
	onChange?: (content: string) => void;
}

export function Editor({ content = '', onChange }: EditorProps): JSX.Element {
	const editor = useEditor({
		extensions: EditorExtensions,
		content,
		editable: true,
		onUpdate: ({ editor }) => {
			onChange?.(editor.getHTML());
		}
	});

	return <EditorContent editor={editor} />;
}

import { JSONContent } from '@tiptap/core';
import { renderToReactElement } from '@tiptap/static-renderer/pm/react';
import { JSX } from 'react';

import { ViewerExtensions } from '../extensions/viewer';

export interface ViewerProps {
	content: JSONContent;
}

export function Viewer({ content }: ViewerProps): JSX.Element {
	return <>{renderToReactElement({ extensions: ViewerExtensions, content })}</>;
}

import { StarterKit } from '@tiptap/starter-kit';

export const Starter = StarterKit.configure({
	horizontalRule: false,
	codeBlock: false
});

// Base editor styles
.notra-editor {
	@apply min-h-[200px] outline-none;
}

// Import component styles
@import 'heading';
@import 'text';
@import 'list';
@import 'code';
@import 'blockquote';

// Blockquote styles
.notra-editor {
	blockquote {
		@apply border-l-4 border-gray-300 dark:border-gray-700 pl-4 italic mb-4 text-gray-700 dark:text-gray-300;
	}
}

// Code styles
.notra-editor {
	code {
		@apply bg-gray-100 dark:bg-gray-800 px-1.5 py-0.5 rounded text-sm font-mono;
	}

	pre {
		@apply bg-gray-900 dark:bg-gray-950 text-gray-100 p-4 rounded-lg mb-4 overflow-x-auto;

		code {
			@apply bg-transparent p-0 text-gray-100;
		}
	}
}

// Heading styles
.notra-editor {
	h1 {
		@apply text-3xl font-bold mb-4 mt-6;
	}

	h2 {
		@apply text-2xl font-bold mb-3 mt-5;
	}

	h3 {
		@apply text-xl font-bold mb-2 mt-4;
	}

	h4 {
		@apply text-lg font-bold mb-2 mt-3;
	}

	h5 {
		@apply text-base font-bold mb-1 mt-2;
	}

	h6 {
		@apply text-sm font-bold mb-1 mt-2;
	}
}

// List styles
.notra-editor {
	ul,
	ol {
		@apply pl-6 mb-3;
	}

	ul {
		@apply list-disc;
	}

	ol {
		@apply list-decimal;
	}

	li {
		@apply mb-1;
	}

	// Nested lists
	ul ul,
	ol ul {
		@apply list-disc;
	}

	ul ol,
	ol ol {
		@apply list-decimal;
	}
}

// Paragraph and text styles
.notra-editor {
	p {
		@apply mb-3;
	}

	strong {
		@apply font-bold;
	}

	em {
		@apply italic;
	}

	a {
		@apply text-blue-600 dark:text-blue-400 underline hover:text-blue-800 dark:hover:text-blue-300;
	}

	hr {
		@apply border-gray-300 dark:border-gray-700 my-6;
	}
}
